import cv2
import numpy as np
import base64

def find_image_position(main_image_base64, smaller_image_base64_list):
    # Decode the main image from base64
    main_image_bytes = base64.b64decode(main_image_base64)
    main_image_np = np.frombuffer(main_image_bytes, np.uint8)
    main_image = cv2.imdecode(main_image_np, cv2.IMREAD_GRAYSCALE)

    # Get the dimensions of the main image
    main_height, main_width = main_image.shape

    # Calculate the middle position
    middle_x = main_width // 2
    middle_y = main_height // 2

    # Store the positions of the smaller images
    positions = []

    for smaller_image_base64 in smaller_image_base64_list:
        # Decode the smaller image from base64
        smaller_image_bytes = base64.b64decode(smaller_image_base64)
        smaller_image_np = np.frombuffer(smaller_image_bytes, np.uint8)
        smaller_image = cv2.imdecode(smaller_image_np, cv2.IMREAD_GRAYSCALE)

        # Perform template matching
        result = cv2.matchTemplate(main_image, smaller_image, cv2.TM_CCOEFF_NORMED)

        # Set a threshold for matches
        threshold = 0.8
        loc = np.where(result >= threshold)

        # Extract the positions of matches
        for pt in zip(*loc[::-1]):
            # Calculate the position relative to the middle of the main image
            position_x = middle_x + (pt[0] - middle_x) // 2
            position_y = middle_y + (pt[1] - middle_y) // 2

            # Add the position to the list
            positions.append((position_x, position_y))

    # Scale the positions to values from 1 to 250
    max_value = max(main_width, main_height)
    positions = [(int(p[0] / max_value * 250), int(p[1] / max_value * 250)) for p in positions]

    return positions


# Example usage
main_image_base64 = ''  # Base64 encoded main image
smaller_image_base64_list = ['', '', '']  # List of base64 encoded smaller images

image_positions = find_image_position(main_image_base64, smaller_image_base64_list)

# Print the positions
for i, position in enumerate(image_positions):
    print(f"Position of smaller image {i+1}: {position}")
